import { Button } from "bootstrap";
import { useState } from "react"


export default function FormularioMaterial(){
    const [data, setData] = useState({
        idMaterial: 0,
        nombreMaterial: "",
        descripcionMaterial:"",
        estadoMaterial: false,
        cantidadMaterial: 0,
        data:[]
    })
    function recibeDatos(event){
        setData({...data, [event.target.name]:event.target.value})
        console.log(data.nombreMaterial)
    }
    function agregarDatos(event){
        event.preventDefault();
        fetch('/addMaterial',{
            method: 'POST',
            body:JSON.stringify(data),
            headers:{
                'Accept' : 'application/json',
                'Content-Type' : 'application/json'
            }
        })
        .then(res => res.json())
        .then(data => ({
            idMaterial: 0,
            nombreMaterial: "",
            descripcionMaterial:"",
            estadoMaterial: false,
            cantidadMaterial: 0
        }))
    }
    return(
        <div className="card">
            <form onSubmit={agregarDatos}>
                <div className="card-header">
                    <h1>Añadir nuevo Material</h1>
                </div>
                <div className="card-body">
                    <label >Id:</label><br/>
                    <input type="text" name="idMaterial" className="form-control" placeholder="código" value={data.idMaterial} onChange={recibeDatos}/><br></br>

                    <label >Material:</label><br/>
                    <input type="text" name="nombreMaterial" className="form-control" placeholder="nombre" value={data.nombreMaterial} onChange={recibeDatos}/><br></br>

                    <label  >Descripcion:</label><br/>
                    <input type="text" name="descripcionMaterial" className="form-control" placeholder="descripción" value={data.descripcionMaterial} onChange={recibeDatos}/><br></br>

                    <label  >Estado:</label><br/>
                    <input type="text" name="estadoMaterial" className="form-control" placeholder="estado" value={data.estadoMaterial} onChange={recibeDatos}/><br></br>

                    <label >Cantidad:</label><br/>
                    <input type="text" name="cantidadMaterial" className="form-control" placeholder="cantidad" value={data.cantidadMaterial} onChange={recibeDatos}/><br></br>


                </div>
                <div className="card-footer">
                    <Button type="submit" className="btn btn-Success">Guardar</Button>

                </div>
            </form>
        </div>
    )
}
