import { Button } from "reactstrap";
import { useEffect, useState } from "react"
import FormularioMaterial from "../../componentes/formulario/FormularioMaterial";

export default function MostrarMaterial({material, coll}){
    const [value, setValue] = useState()
    useEffect(() => {
            if(value){
                fetch(`/deleteMaterial/${value}`,{
                    method: 'DELETE',
                    headers:{
                        'Accept' : 'application/json',
                        'Content-Type' : 'application/json'
                    }
                })
                .then(res => res.json())
            }
    })

    function collapse(valor){
        console.log('formulario....: ', valor.nombreMaterial);
        <FormularioMaterial band={valor}/>
    }
    return(
        <div>
            <br/>
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">Código</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Estado</th>
                    <th scope="col">Cantidad</th>
                    <th scope="col">        </th>
                    </tr>
                </thead>
                <tbody>     
                {
                    material.map((valor,cont)=>(
                        <tr key={cont}>
                            <th scope="row">{valor.idMaterial}</th>
                            <td>{valor.nombreMaterial}</td>
                            <td>{valor.descripcionMaterial}</td>
                            <td>{valor.estadoMaterial}</td>
                            <td>{valor.cantidadMaterial}</td>
                            <td >                            
                                <p>
                                    <button className="btn btn-warning" onClick={collapse(valor)} type="button" data-bs-toggle="collapse" data-bs-target="#collapseExample" aria-expanded="false" aria-controls="collapseExample">
                                        Editar
                                    </button>
                                </p>
                                {/* <div class="collapse" id="collapseExample">
                                    <div class="card card-body">

                                    </div>
                                </div> */}
                            </td>
                            <td>
                                <Button className="btn btn-danger" onClick={() => setValue(valor._id)}>Eliminar </Button>
                            </td>
                        </tr>
                    ))
                }                  

                </tbody>
            </table>

        </div>
    )
}
