import { Button } from "reactstrap";
import { useEffect, useState } from "react"
export default function MostrarMaterial({material}){
    const [value, setValue] = useState()
    useEffect(() => {
            if(value){
                fetch(`/deleteMaterial/${value}`,{
                    method: 'DELETE',
                    headers:{
                        'Accept' : 'application/json',
                        'Content-Type' : 'application/json'
                    }
                })
                .then(res => res.json())
            }
    })
    return(
        <div>
            <br/>
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">Código</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Estado</th>
                    <th scope="col">Cantidad</th>
                    <th scope="col">        </th>
                    </tr>
                </thead>
                <tbody>     
                {
                    material.map((valor,cont)=>(
                        <tr key={cont}>
                            <th scope="row">{valor.idMaterial}</th>
                            <td>{valor.nombreMaterial}</td>
                            <td>{valor.descripcionMaterial}</td>
                            <td>{valor.estadoMaterial}</td>
                            <td>{valor.cantidadMaterial}</td>
                            <td class="d-grid gap-2 col-6 mx-auto">
                                <Button className="btn btn-warning" onClick={() => setValue(valor._id)}>Editar</Button>
                            </td>
                            <td>
                                <Button className="btn btn-danger" onClick={() => setValue(valor._id)}>Eliminar</Button>
                            </td>
                        </tr>
                    ))
                }                  

                </tbody>
            </table>

        </div>
    )
}
