import { Button } from "reactstrap";
import { useEffect, useState } from "react"
import FormularioMaterial from "../../componentes/formulario/FormularioMaterial";

export default function MostrarMaterial({material, coll}){
    const [value, setValue] = useState()
    useEffect(() => {
            if(value){
                fetch(`/deleteMaterial/${value}`,{
                    method: 'DELETE',
                    headers:{
                        'Accept' : 'application/json',
                        'Content-Type' : 'application/json'
                    }
                })
                .then(res => res.json())
            }
    })
    const [valueEditar, setValueEdit] = useState()
    useEffect(() => {
            if(valueEditar){
                console.log("mensaje");
            }
    })

    return(
        <div>
            <br/>
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">Código</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Estado</th>
                    <th scope="col">Cantidad</th>
                    <th scope="col">        </th>
                    </tr>
                </thead>
                <tbody>     
                {
                    material.map((valor,cont)=>(
                        <tr key={cont}>
                            <th scope="row">{valor.idMaterial}</th>
                            <td>{valor.nombreMaterial}</td>
                            <td>{valor.descripcionMaterial}</td>
                            <td>{valor.estadoMaterial}</td>
                            <td>{valor.cantidadMaterial}</td>
                            <td >                            
                                <button type="button" class="btn btn-warning" onClick={() => setValueEdit(valor._id)} data-bs-toggle="modal" data-bs-target="#exampleModal">
                                Editar
                                </button>
                            </td>
                            <td>
                                <Button className="btn btn-danger" onClick={() => setValue(valor._id)}>Eliminar </Button>
                            </td>
                        </tr>
                    ))
                }                  
                </tbody>
            </table>
            <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Materiales</h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            <h1>{valueEditar}</h1>
                            <FormularioMaterial band={valueEditar}/>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancelar</button>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    )
}
